    var self = this;
    if(_.isNumber(milliseconds) ) {
      setTimeout(self.flush.bind(self), milliseconds);
    }
    else {
      var attempts = self.attempts;
      self.attempts = [];
      _.each(attempts, function(x) {
        x[0].apply(self, x.slice(1));
      });
    }
    return self;
